import React, {useEffect, useState} from 'react';
import './App.css';
import BoardListItemFunc from "./components/BoardItem";
import {latestBoardListMock, top3BoardListMock, commentListMock, favoriteListMock} from "./mocks";
import Top3Item from "./components/Top3Item";
import CommentItem from "./components/CommentItem";
import FavoriteItem from "./components/FavoriteItem";
import InputBox from "./components/InputBox";
import Footer from "./layouts/Footer";
import {Route, Routes} from "react-router-dom";
import Main from "./views/Main";
import Authentication from "./views/Authentication";
import Search from "./views/Search";
import UserP from "./views/User";
import BoardDetail from "./views/Board/Detail";
import BoardWrite from "./views/Board/Write";
import BoardUpdate from "./views/Board/Update";
import Container from "./layouts/Container";
import {
    AUTH_PATH,
    BOARD_DETAIL_PATH,
    BOARD_PATH, BOARD_UPDATE_PATH,
    BOARD_WRITE_PATH,
    MAIN_PATH,
    SEARCH_PATH,
    USER_PATH
} from "./constant";
import {useCookies} from "react-cookie";
import {useLoginUserStore} from "./stores";
import {getSignInUserRequest} from "./apis";
import {ResponseDto} from "./apis/response";
import {GetSignInUserResponseDto} from "./apis/response/user";
import {User} from "./types/interface";

// component: Application 컴포넌트
function App() {

    // state: 로그인 유저 전역 상태
    const {setLoginUser, resetLoginUser} = useLoginUserStore();
    // state: cookie 상태
    const [cookies, setCookies] = useCookies();

    // function: get sign in user response 처리 함수
    const getSignInUserResponse = (responseBody: GetSignInUserResponseDto | ResponseDto | null) => {
        if (!responseBody) return;
        const {code} = responseBody;
        if (code === 'AF' || code === 'NU' || code === 'DBE') {
            resetLoginUser();
            return;
        }
        const loginuser: User = {...responseBody as GetSignInUserResponseDto}; //...은 spread 연산자
        setLoginUser(loginuser);
    }

    // effect: accessToken cookie 값이 변경될 때마다 실행할 함수
    useEffect(() => {
        if (!cookies.accessToken) {
            resetLoginUser();
            return;
        }
        getSignInUserRequest(cookies.accessToken).then(getSignInUserResponse);
    }, [cookies.accessToken]);


    // render: Application 컴포넌트 랜더링
    // description: 메인화면: '/' - Main
    // description: 로그인 + 회원가입 화면: '/auth' - Authentication
    // description: 검색화면: '/search/:word' - Search
    // description: 유저 페이지: '/user/:userEmail' - User
    // description: 게시물 상세보기: '/board/detail/:boardNumber' - BoardDetail
    // description: 게시물 작성하기: '/board/write' - BoardWrite
    // description: 게시물 수정하기: '/board/update/:boardNumber' - BoardUpdate



    return (
    <Routes>
        <Route element={<Container />}>
            <Route path={MAIN_PATH()} element={<Main/>}/>
            <Route path={AUTH_PATH()} element={<Authentication/>}/>
            <Route path={SEARCH_PATH(':searchWord')} element={<Search/>}/>
            <Route path={USER_PATH(':userEmail')} element={<UserP/>}/>
            <Route path= {BOARD_PATH()}>
                <Route path={BOARD_DETAIL_PATH(':boardNumber')} element={<BoardDetail/>}/>
                <Route path={BOARD_WRITE_PATH()} element={<BoardWrite/>}/>
                <Route path={BOARD_UPDATE_PATH(':boardNumber')} element={<BoardUpdate/>}/>
            </Route>
            <Route path="*" element={<div>404 NOT FOUND</div>}/>
        </Route>

    </Routes>
  );
}

export default App;
